para.example:
      get_class_methodsの例


<?php

class myclass {
    // コンストラクタ
    function myclass()
    {
        return(true);
    }

    // メソッド1
    function myfunc1()
    {
        return(true);
    }

    // メソッド2
    function myfunc2()
    {
        return(true);
    }
}

$my_class = new myclass();

$class_methods = get_class_methods(get_class($my_class));

foreach ($class_methods as $method_name) {
    echo "$method_name\n";
}

?>


      &example.outputs;


myclass
myfunc1
myfunc2
