refsect1.para:
     文字列は、ともに秒単位で返されます。

      microtime の例


function getmicrotime(){ 
    list($usec, $sec) = explode(" ",microtime()); 
    return ((float)$sec + (float)$usec); 
    } 

$time_start = getmicrotime();

for ($i=0; $i < 1000; $i++){
    // 何もしない。1000 回ループ
    }

$time_end = getmicrotime();
$time = $time_end - $time_start;

echo "$time 秒間何もしませんでした。";
