refsect1.para:
     ibase_query または ibase_execute
      により得られた result_id から
     擬似オブジェクトとして 1 行分の結果を取得します。



<?php
    $dbh = ibase_connect($host, $username, $password);
    $stmt = 'SELECT * FROM tblname';
    $sth = ibase_query($dbh, $stmt);
    while ($row = ibase_fetch_object($sth)) {
        echo $row->email . "\n";
    }
    ibase_close($dbh);
?>
