refentry.refsect1:
  &reftitle.description;

&style.procedural;

   boolmaxdb_kill
   resourcelink
   intprocessid


&style.oop;

   boolmaxdb::kill
   intprocessid



   この関数は、processid で指定した
   MaxDB サーバから切断するために使用されます。

  &reftitle.examples;

   &style.oop;


<?php
$maxdb = new maxdb("localhost", "MONA", "RED", "DEMODB");

/* 接続を調べます */
if (maxdb_connect_errno()) {
   printf("接続に失敗しました: %s\n", maxdb_connect_error());
   exit();
}

/* スレッド ID を調べます */
$thread_id = $maxdb->thread_id;

/* 接続を殺します */
$maxdb->kill($thread_id);

/* これは、エラーとなります */
if (!$maxdb->query("CREATE TABLE myCity LIKE City")) {
   printf("エラー: %s\n", $maxdb->error);
   exit;
}

/* 接続を閉じます */
$maxdb->close();
?>




   &style.procedural;


<?php
$link = maxdb_connect("localhost", "MONA", "RED", "DEMODB");

/* 接続を調べます */
if (maxdb_connect_errno()) {
   printf("接続に失敗しました: %s\n", maxdb_connect_error());
   exit();
}

/* スレッド ID を調べます */
$thread_id = maxdb_thread_id($link);

/* 接続を殺します */
maxdb_kill($link, $thread_id);

/* これは、エラーとなります */
if (!maxdb_query($link, "CREATE TABLE myCity LIKE City")) {
   printf("エラー: %s\n", maxdb_error($link));
   exit;
}

/* 接続を閉じます */
maxdb_close($link);
?>



