refentry.refsect1:
  &reftitle.examples;



    Phar->isFlushingToPhar の例





<?php
$p = new Phar(dirname(__FILE__) . '/brandnewphar.phar', 0, 'brandnewphar.phar');
$p2 = new Phar('existingphar.phar');
$p['file1.txt'] = 'hi';
var_dump($p->isFlushingToPhar());
var_dump($p2->isFlushingToPhar());
?>
=2=
<?php
$p->begin();
var_dump($p->isFlushingToPhar());
var_dump($p2->isFlushingToPhar());
$p->commit();
?>
=3=
<?php
var_dump($p->isFlushingToPhar());
var_dump($p2->isFlushingToPhar());
?>


    &example.outputs;


bool(true)
bool(true)
=2=
bool(false)
bool(true)
=3=
bool(true)
bool(true)
