refentry.refsect1:
&reftitle.returnvalues;


 成功時に SESAM "結果 ID"、またはエラー時に &false; を返します。
 この結果 ID を、その他の関数で使用します。

  &reftitle.examples;




     "phone" テーブルの全てのレコードを HTML テーブルとして表示する



<?php
if (!sesam_connect("phonedb", "demo", "otto"))
    die("接続できません");
$result = sesam_query("select * from phone");
if (!$result) {
    $err = sesam_diagnostic();
    die ($err["errmsg"]);
}
echo "<table border>\n";
// 結果の上にカラム名をヘッダとして表示します
if ($cols = sesam_field_array($result)) {
    echo "<tr><th colspan=" . $cols["count"] . ">Result:</th></tr>\n";
    echo "<tr>\n";
    for ($col = 0; $col < $cols["count"]; ++$col) {
        $colattr = $cols[$col];
        /* SESAM の "Multiple Fields" について、列を連結します */
        if ($colattr["count"] > 1) {
            echo "<th colspan=\"" . $colattr["count"] . "\">" . $colattr["name"] .
                "(1.." . $colattr["count"] . ")</th>\n";
            $col += $colattr["count"] - 1;
        } else
            echo "<th>" . $colattr["name"] . "</th>\n";
    }
    echo "</tr>\n";
}

do {
    // 最大 100 行までに分割して結果を取得します
    $ok = sesam_fetch_result($result, 100);
    for ($row=0; $row < $ok["rows"]; ++$row) {
        echo " <tr>\n";
        for ($col = 0; $col < $ok["cols"]; ++$col) {
            if (isset($ok[$col][$row])) {
                echo "<td>" . $ok[$col][$row] . "</td>\n";
            } else {
                echo "<td>-empty-</td>\n";
            }
        }
        echo "</tr>\n";
    }
} while ($ok["truncated"]); // データがなくなるまで続けます

echo "</table>\n";
// 結果 id を開放します
sesam_free_result($result);
?>
