    spl_autoload_register の例


<?php

namespace Foobar;

class Foo {
    static public function test($name) {
        print '[['. $name .']]';
    }
}

spl_autoload_register(__NAMESPACE__ .'::Foo::test'); // PHP 5.3.0 以降

new InexistentClass;

?>


